package com.autocine.defam.services;

import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.LinkedList;
import java.util.List;
import java.util.Optional;

import org.springframework.stereotype.Service;

import com.autocine.defam.model.Pelicula;

@Service
public class PeliculaServicesImpl implements IPeliculaService {
    private final List<Pelicula> peliculas = new LinkedList<>();
    private final List<Pelicula> estrenos = new LinkedList<>();

    public PeliculaServicesImpl() {
        // Se crea la lista de objetos de tipo Pelicula
        SimpleDateFormat sdf = new SimpleDateFormat("dd-MM-yyyy");

        try {
            // Creamos la película 1
            Pelicula pelicula1 = new Pelicula(
                    "Inception",
                    148,
                    "Ciencia ficción",
                    2010,
                    "https://images.vix.com/prd/videos/video:mcp:4563715/87754705da512ee8e9138d9168c2a402?tr=w-196,ar-2-3,q-80&w=1920",
                    "Un ladrón con la rara habilidad de 'robar' secretos de las mentes de las personas mientras sueñan.",
                    sdf.parse("16-07-2010"));

            // Creamos la película 2
            Pelicula pelicula2 = new Pelicula(
                    "The Shawshank Redemption",
                    142,
                    "Drama",
                    1994,
                    "https://images.vix.com/prd/videos/video:mcp:4235428/ae1c38bf55d6bc731b3301fccc7cd74c?tr=w-196,ar-2-3,q-80&w=1920",
                    "Dos hombres encarcelados se unen a lo largo de varios años, encontrando consuelo y eventual redención a través de actos de decencia común.",
                    sdf.parse("14-10-1994"));
            peliculas.add(new Pelicula(
                    "The Lion King", 88, "Animation", 1994,
                    "data:image/jpeg;base64,",
                    "El viaje épico de un joven león llamado Simba para convertirse en el rey de la sabana.",
                    sdf.parse("15-06-1994")));
            peliculas.add(new Pelicula(
                    "Gladiator", 155, "Action", 2000,
                    "https://es.web.img3.acsta.net/medias/nmedia/18/70/92/02/20149073.jpg",
                    "Un ex general es obligado a convertirse en gladiador y busca venganza contra el emperador que traicionó a su familia.",
                    sdf.parse("05-05-2000")));
            peliculas.add(new Pelicula(
                    "Star Wars: Episode V - The Empire Strikes Back", 124, "Sci-Fi", 1980,
                    "https://m.media-amazon.com/images/I/91y-L-AEa4L._AC_UF894,1000_QL80_.jpg",
                    "Luke Skywalker y la Alianza Rebelde enfrentan al Imperio Galáctico, mientras descubre la verdad sobre su familia.",
                    sdf.parse("21-05-1980")));
            peliculas.add(new Pelicula(
                    "Interstellar", 169, "Sci-Fi", 2014,
                    "https://m.media-amazon.com/images/S/pv-target-images/79194981293eabf6620ece96eb5a9c1fffa04d3374ae12986e0748800b37b9cf.jpg",
                    "Un grupo de exploradores viajan a través de un agujero de gusano en el espacio para asegurar la supervivencia de la humanidad.",
                    sdf.parse("07-11-2014")));
            peliculas.add(new Pelicula(
                    "Pulp Fiction", 154, "Crime", 1994,
                    "https://es.web.img3.acsta.net/img/05/66/05663f00b8b5df58b003aaf5c46ef8ad.jpg",
                    "Las vidas de dos sicarios, un boxeador, la esposa de un gánster y dos bandidos se entrelazan en cuatro cuentos de violencia y redención.",
                    sdf.parse("14-10-1994")));
            peliculas.add(new Pelicula(
                    "Inception", 148, "Sci-Fi", 2010,
                    "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_.jpg",
                    "Un ladrón con la rara habilidad de 'robar' secretos de las mentes de las personas mientras sueñan.",
                    sdf.parse("16-07-2010")));
            peliculas.add(new Pelicula(
                    "Fight Club", 139, "Drama", 1999,
                    "https://m.media-amazon.com/images/M/MV5BOTgyOGQ1NDItNGU3Ny00MjU3LTg2YWEtNmEyYjBiMjI1Y2M5XkEyXkFqcGc@._V1_.jpg",
                    "Un hombre insomne y un vendedor de jabón forman un club de lucha clandestino que evoluciona en algo mucho más.",
                    sdf.parse("15-10-1999")));
            estrenos.add(new Pelicula(
                    "Forrest Gump", 142, "Drama", 1994,
                    "https://pics.filmaffinity.com/Forrest_Gump-212765827-large.jpg",
                    "La vida de Forrest Gump, un hombre con una inteligencia limitada pero con una serie de experiencias extraordinarias.",
                    sdf.parse("06-07-1994")));
            estrenos.add(new Pelicula(
                    "Matrix", 136, "Sci-Fi", 1999,
                    "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_.jpg",
                    "Un hacker descubre la verdadera naturaleza de su realidad y su papel en la guerra contra sus controladores.",
                    sdf.parse("31-03-1999")));
            estrenos.add(new Pelicula(
                    "The Lord of the Rings: The Return of the King", 201, "Fantasy", 2003,
                    "https://es.web.img3.acsta.net/medias/nmedia/18/89/67/45/20061512.jpg",
                    "Aragorn revelará su verdadera identidad como el rey de Gondor y conducirá a sus seguidores a una última batalla contra el malvado Sauron.",
                    sdf.parse("17-12-2003")));
            estrenos.add(new Pelicula(
                    "The Godfather", 175, "Crime", 1972,
                    "data:image/jpeg;base64,",
                    "El patriarca de una familia criminal transferirá el control de su imperio clandestino a su renuente hijo.",
                    sdf.parse("24-03-1972")));
            estrenos.add(new Pelicula(
                    "The Dark Knight", 152, "Action", 2008,
                    "https://m.media-amazon.com/images/M/MV5BMTMxNTMwODM0NF5BMl5BanBnXkFtZTcwODAyMTk2Mw@@._V1_.jpg",
                    "Cuando el Joker irrumpe en la vida de Gotham, Batman debe enfrentarse a su mayor reto hasta el momento.",
                    sdf.parse("18-07-2008")));
            // Agregamos las películas a la lista
            peliculas.add(pelicula1);
            peliculas.add(pelicula2);

        } catch (ParseException e) {
            System.out.println("Error: " + e.getMessage());
        }
    }

    @Override
    public void agregarPelicula(Pelicula pelicula) {
        peliculas.add(pelicula);
    }

    @Override
    public List<Pelicula> obtenerTodasLasPeliculas() {
        return peliculas;
    }

    @Override
    public Pelicula obtenerPeliculaPorTitulo(String titulo) {
        Optional<Pelicula> pelicula = peliculas.stream()
                .filter(p -> p.getTitulo().equalsIgnoreCase(titulo))
                .findFirst();
        return pelicula.orElse(null);
    }

    @Override
    public void eliminarPelicula(String titulo) {
        peliculas.removeIf(p -> p.getTitulo().equalsIgnoreCase(titulo));
    }

    @Override
    public List<Pelicula> obtenerEstrenos() {
        return estrenos;
    }
}
